mapper.select:
select count(*)
  from tb_like
 where id = #{id}

select count(*)
  from tb_like
 where id = #{id}
   and hosp_code = #{hospCode}

mapper.insert:
insert into tb_like(
	id, hosp_code, name, main_treat
) values(
	#{id}, #{hospCode}, #{name}, #{mainTreat}
)

mapper.delete:
delete
  from tb_like
 where id = #{id}
   and hosp_code = #{hospCode}
